var blue = 255;

var heat = new Array();
for (var a = 0; a < COLOR_LED_COUNT; a++) {
    heat.push(0);
}
var cooldown;


function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min)) + min; //The maximum is exclusive and the minimum is inclusive
}

// Step 1.  Cool down every cell a little
for( var i = 0; i < COLOR_LED_COUNT; i++) {
    cooldown = getRandomInt(0, ((cooling * 10) / COLOR_LED_COUNT) + 2);

    if(cooldown>heat[i]) {
      heat[i]=0;
    } else {
      heat[i]=heat[i]-cooldown;
    }
}

// Step 2.  Heat from each cell drifts 'up' and diffuses a little
for( var k = COLOR_LED_COUNT - 1; k >= 2; k--) {
    heat[k] = (heat[k - 1] + heat[k - 2] + heat[k - 2]) / 3;

}

// Step 3.  Randomly ignite new 'sparks' near the bottom
if( getRandomInt(0,255) < sparking ) {
    var y = getRandomInt(0,COLOR_LED_COUNT);
    heat[y] = heat[y] + getRandomInt(100,255);
    //heat[y] = random(160,255);

}

// Step 4.  Convert heat to LED colors
for( var pixel = 0; pixel < COLOR_LED_COUNT; pixel++) {

    // Scale 'heat' down from 0-255 to 0-191
  var t192 = Math.round((heat[pixel]/255)*220);


  // calculate ramp up from
  var heatramp = t192 & 0x3F; // 0..63

  heatramp <<= 2; // scale up to 0..252

  // figure out which third of the spectrum we're in:
  if( t192 > 0x80) {                     // hottest
    node.send({"payload": (COLOR_LED_COUNT - pixel - 1) + ",255,255," + heatramp });
  } else if( t192 > 0x40 ) {             // middle
    node.send({"payload": (COLOR_LED_COUNT - pixel - 1) + ",255," + heatramp + ",0"});
  } else {                               // coolest
    node.send({"payload": (COLOR_LED_COUNT - pixel - 1) + "," + heatramp + ",0,0"});
  }

}

